import { Component } from 'react'
import './PyTCalculator.css'
class TipCalculator extends Component{
    constructor (props){
        super(props)
        this.state={
            valA: 0,
            valB: 0, 
            valC: 1
        }
    }
    render (){
        return(
            <form className='form'>
                <h1>Pythagorean Theorum: A^2+B^2=C^2</h1>
                <h1>valA: {this.state.valA}</h1>
                <h1>valB: {this.state.valB}</h1>
                <h1>valC: {this.state.valC}</h1>

                <button onclick={(e) => {this.setState({valA: (this.state.valC**2)-(this.state.valB**2)})}}>Calculate A</button>
                <label for="Bill">Bill: </label>
                <input
                    id='Bill'
                    placeholder = 'Bill'
                    type = 'number'
                    value = {this.state.bill}
                    onChange = {(e) => {this.setState({bill: e.target.value})}}
                />
                <label for="Tip">Tip %: </label>
                <input
                    id='Tip'
                    placeholder = 'Tip'
                    type = 'number'
                    value = {this.state.tip}
                    onChange = {(e) => {this.setState({tip: e.target.value})}}
                />
                <label for="People to Split Upon">People to Split Upon: </label>
                <input
                    id='People to Split Upon'
                    placeholder = 'People to Split Upon'
                    type = 'number'
                    value = {this.state.people}
                    onChange = {(e) => {this.setState({people: e.target.value})}}
                />
            </form>
        )
    }
}
export default TipCalculator
