import { Component } from 'react'
import './PyTCalculator.css'
class PyTCalculator extends Component{
    constructor (props){
        super(props)
        this.state={
            valA: 0,
            valB: 0, 
            valC: 1,
            output: ''
        }
    }
    render (){
        return(
            <form className='form'>
                <h1>Pythagorean Theorum: A^2+B^2=C^2</h1>
                <h1>valA: {this.state.valA}</h1>
                <h1>valB: {this.state.valB}</h1>
                <h1>valC: {this.state.valC}</h1>
                <h1>{this.state.output}</h1>


                <button onclick={(e) => {this.setState({valA: (this.state.valC**2)-(this.state.valB**2)})}}>Calculate A</button>

                <label for="ValA">ValA: </label>
                <input
                    id='ValA'
                    placeholder = 'ValA'
                    type = 'number'
                    value = {this.state.valA}
                    onChange = {(e) => {this.setState({valA: e.target.value})}}
                />
                <label for="ValB">ValB: </label>
                <input
                    id='ValB'
                    placeholder = 'ValB'
                    type = 'number'
                    value = {this.state.valB}
                    onChange = {(e) => {this.setState({valB: e.target.value})}}
                />
                <label for="ValC">ValC: </label>
                <input
                    id='ValC'
                    placeholder = 'ValC'
                    type = 'number'
                    value = {this.state.valC}
                    onChange = {(e) => {this.setState({valC: e.target.value})}}
                />

            </form>
        )
    }
}
export default PyTCalculator
